</template>

<script>
    export default {
        props: ['contracts', 'network', 'ethereumPrice'],
        data() {
            return { 
                parsedContracts: []
            }
        },
        mounted() {
            this.getBalances()
        },
        methods: {
            async getBalances() {
                this.parsedContracts = JSON.parse(this.contracts)

